import React from "react";
import carServices from "../services/carServices";
import {Link, useSearchParams} from 'react-router-dom';
import {useState, useEffect} from 'react';



class CarComponent extends React.Component {

       
        
        constructor(props) {
            super(props)
            this.state = {
                cars:[]
            }
        }

        componentDidMount() {
            carServices.getCars().then((response) => {
                this.setState({cars: response.data})
            });
        }

        render = () => {

            const [cars, setCars] = useState([]);
            const [searchParams, setSearchParams] = useSearchParams();

            const carQuery = searchParams.get('car') || '';

            const handleSubmit = (e) => {
                e.preventDefault();
                const form = e.target;

                const query = form.search.value;

                setSearchParams({post: query})
            } 
            // useEffect(() => {
            //     fetch('https://localhost:8080/cars')
            //     .then(res => res.json())
            //     .then(data => setCars(data))
            // }, []);

            return (
                <div>
                    <h1>AutoShow.by</h1>
                    <hr></hr>
                    <form autoComplete="off" onSubmit={handleSubmit}>
                        <input type="search" name="search"/>
                        <input type="submit" value="Search"/>
                    </form>
                    {
                        this.state.cars.map(
                            car =>
                        <div class="search" key = {car.id}>
                            <h6><Link to={`cars/${car.carBrand}`}>{car.carBrand}</Link></h6>
                        </div>
                        )
                    }
                    
                    <div class="cars">
                    {
                                this.state.cars.filter(
                                    car => car.title.includes(carQuery)
                                ).map(
                                    car =>
                                    <div class="car" key = {car.id}>
                                        <div class="car-img">
                                            <p><img src ={car.imageCar}></img></p>
                                        </div>
                                        <div class="car-info-all">
                                            <div class="car-info">
                                                <h4><Link to={`car/${car.id}`}> {car.carBrand} {car.model}</Link></h4>
                                                <p>Год выпуска: {car.yearRelease}</p>
                                            </div>
                                            <div class="prise-car">
                                                <h4>{car.prise}$</h4>
                                            </div>
                                        </div>
                                    </div>
                                )
                            }
                    </div>
                </div>
                
            )
        }
}

export default CarComponent
